// Sidebar component
const Sidebar: React.FC = () => (
  <nav className="sidebar">
    <ul>
      <li><Link to="/">Task Listing</Link></li>
      <li><Link to="/taskformpage">Task Form</Link></li>
    </ul>
  </nav>
);

// Main App component with Router and Sidebar
const App: React.FC = () => {
  return (
    <Router>
      <div className="app-container">
        <Sidebar />
        <div className="main-content">
          <Routes>
            <Route path="/" element={<TaskList />} />
            <Route path="/taskformpage" element={<TaskFormPage />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};
