import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import './App.css';
import TaskFormPage from './pages/TaskFormPage';

interface Task {
  id: number;
  name: string;
  status: 'IN_PROGRESS' | 'DONE' | 'COMPLETE';
  parent_task_id: number | null;
}

const TaskList: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [filter, setFilter] = useState<'ALL' | 'IN_PROGRESS' | 'DONE' | 'COMPLETE'>('ALL');

  // Fetch tasks from the backend
  const fetchTasks = async () => {
    try {
      const response = await fetch('http://localhost:5000/tasks');
      if (!response.ok) {
        throw new Error('Failed to fetch tasks');
      }
      const data: Task[] = await response.json();
      setTasks(data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  // Toggle task status
  const toggleTaskStatus = async (task: Task) => {
    const updatedStatus = task.status === 'IN_PROGRESS' ? 'DONE' : 'IN_PROGRESS';

    try {
      await fetch(`http://localhost:5000/tasks/${task.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ...task, status: updatedStatus }),
      });

      window.location.reload();

      // Update the state locally after a successful update
      setTasks(prevTasks =>
        prevTasks.map(t =>
          t.id === task.id ? { ...t, status: updatedStatus } : t
        )
      );
    } catch (error) {
      console.error('Error updating task status:', error);
    }
  };

  // Filter tasks based on selected status
  const filteredTasks = filter === 'ALL' ? tasks : tasks.filter(task => task.status === filter);

  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div>
      <h1>Task Listing</h1>
      <label>
        Filter by status:
        <select value={filter} onChange={(e) => setFilter(e.target.value as 'ALL' | 'IN_PROGRESS' | 'DONE' | 'COMPLETE')}>
          <option value="ALL">All</option>
          <option value="IN_PROGRESS">IN PROGRESS</option>
          <option value="DONE">DONE</option>
          <option value="COMPLETE">COMPLETE</option>
        </select>
      </label>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Status</th>
            <th>Parent Task ID</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredTasks.map(task => (
            <tr key={task.id}>
              <td>{task.id}</td>
              <td>{task.name}</td>
              <td>{task.status}</td>
              <td>{task.parent_task_id !== null ? task.parent_task_id : 'N/A'}</td>
              <td>
                <label>
                  <input
                    type="checkbox"
                    checked={task.status === 'DONE' || task.status ==='COMPLETE'}
                    onChange={() => toggleTaskStatus(task)}
                  />
                  {task.status === 'DONE' || task.status === 'COMPLETE' ? ' Mark as IN PROGRESS' : ' Mark as DONE'}
                </label>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Sidebar component
const Sidebar: React.FC = () => (
  <nav className="sidebar">
    <ul>
      <li><Link to="/">Task Listing</Link></li>
      <li><Link to="/taskformpage">Task Form</Link></li>
    </ul>
  </nav>
);

// Main App component with Router and Sidebar
const App: React.FC = () => {
  return (
    <Router>
      <div className="app-container">
        <Sidebar />
        <div className="content">
          <Routes>
            <Route path="/" element={<TaskList />} />
            <Route path="/taskformpage" element={<TaskFormPage/>} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
