import React, { useEffect, useState, useCallback } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import './App.css';
import TaskFormPage from './pages/TaskFormPage';

interface Task {
  id: number;
  name: string;
  status: 'IN_PROGRESS' | 'DONE' | 'COMPLETE';
  parent_task_id: number | null;
  dependencyCount?: number;
  doneCount?: number;
  completeCount?: number;
  children: Task[];
}

const RenderTasks = ({ tasks, toggleTaskStatus, handleEditTask, editingTaskId, newTaskName, setNewTaskName, saveEdit }: {
  tasks: Task[];
  toggleTaskStatus: (task: Task) => void;
  handleEditTask: (task: Task) => void;
  editingTaskId: number | null;
  newTaskName: string;
  setNewTaskName: (name: string) => void;
  saveEdit: () => void;
}) => (
  <div className="task-list">
    {tasks.map(task => (
      <div key={task.id} className="task-item">
        <div className="task-header">
          {editingTaskId === task.id ? (
            <>
              <input
                type="text"
                value={newTaskName}
                onChange={(e) => setNewTaskName(e.target.value)}
                placeholder="New task name"
              />
              <button onClick={saveEdit}>Save</button>
              <button onClick={() => handleEditTask(null)}>Cancel</button>
            </>
          ) : (
            <>
              <strong onDoubleClick={() => handleEditTask(task)}>
                Task {task.name} (ID: {task.id})
              </strong> - Status: {task.status}
              <div className="task-counts">
                Dependencies: {task.dependencyCount || 0} | Done: {task.doneCount || 0} | Complete: {task.completeCount || 0}
              </div>
              <label className="task-checkbox">
                <input
                  type="checkbox"
                  checked={task.status === 'DONE' || task.status === 'COMPLETE'}
                  onChange={() => toggleTaskStatus(task)}
                />
                {task.status === 'DONE' || task.status === 'COMPLETE' ? ' Mark as IN PROGRESS' : ' Mark as DONE'}
              </label>
            </>
          )}
        </div>
        {task.children.length > 0 && (
          <div className="child-tasks">
            <RenderTasks
              tasks={task.children}
              toggleTaskStatus={toggleTaskStatus}
              handleEditTask={handleEditTask}
              editingTaskId={editingTaskId}
              newTaskName={newTaskName}
              setNewTaskName={setNewTaskName}
              saveEdit={saveEdit}
            />
          </div>
        )}
      </div>
    ))}
  </div>
);

const TaskList: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [filter, setFilter] = useState<'ALL' | 'IN_PROGRESS' | 'DONE' | 'COMPLETE'>('ALL');
  const [editingTaskId, setEditingTaskId] = useState<number | null>(null);
  const [newTaskName, setNewTaskName] = useState<string>('');

  const fetchTasks = useCallback(async () => {
    try {
      const response = await fetch('http://localhost:5000/tasks');
      const data: Task[] = await response.json();
      setTasks(data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  }, []);

  const toggleTaskStatus = async (task: Task) => {
    // Status update logic
  };

  const handleEditTask = (task: Task | null) => {
    if (task) {
      setEditingTaskId(task.id);
      setNewTaskName(task.name);
    } else {
      setEditingTaskId(null);
      setNewTaskName('');
    }
  };

  const saveEdit = async () => {
    if (editingTaskId !== null) {
      try {
        const response = await fetch(`http://localhost:5000/tasks/edit-name/${editingTaskId}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ name: newTaskName }),
        });

        if (!response.ok) {
          throw new Error('Failed to update task name');
        }

        setTasks(prevTasks =>
          prevTasks.map(t => (t.id === editingTaskId ? { ...t, name: newTaskName } : t))
        );
        setEditingTaskId(null);
        setNewTaskName('');
      } catch (error) {
        console.error('Error updating task name:', error);
      }
    }
  };

  const filteredTasks = tasks.filter(task => {
    if (filter === 'ALL') return true;
    if (filter === 'IN_PROGRESS') return task.status === 'IN_PROGRESS';
    if (filter === 'DONE') return task.status === 'DONE';
    if (filter === 'COMPLETE') return task.status === 'COMPLETE';
    return false;
  });

  const taskHierarchy = filteredTasks.map(task => ({
    ...task,
    children: tasks.filter(t => t.parent_task_id === task.id),
  }));

  useEffect(() => {
    fetchTasks();
  }, [fetchTasks]);

  return (
    <div className="task-list-container">
      <h1>Task Listing</h1>
      <label>
        Filter by status:
        <select value={filter} onChange={(e) => setFilter(e.target.value as 'ALL' | 'IN_PROGRESS' | 'DONE' | 'COMPLETE')}>
          <option value="ALL">All</option>
          <option value="IN_PROGRESS">IN PROGRESS</option>
          <option value="DONE">DONE</option>
          <option value="COMPLETE">COMPLETE</option>
        </select>
      </label>
      <div className="task-list">
        <RenderTasks
          tasks={taskHierarchy}
          toggleTaskStatus={toggleTaskStatus}
          handleEditTask={handleEditTask}
          editingTaskId={editingTaskId}
          newTaskName={newTaskName}
          setNewTaskName={setNewTaskName}
          saveEdit={saveEdit}
        />
      </div>
    </div>
  );
};

const App: React.FC = () => {
  return (
    <Router>
      <div className="app-container">
        <div className="sidebar">
          <h2>Sidebar</h2>
          <ul>
            <li><Link to="/">Task List</Link></li>
            <li><Link to="/new-task">Add New Task</Link></li>
          </ul>
        </div>
        <div className="main-content">
          <Routes>
            <Route path="/" element={<TaskList />} />
            <Route path="/new-task" element={<TaskFormPage />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
