import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import './App.css';
import TaskFormPage from './pages/TaskFormPage';

interface Task {
  id: number;
  name: string;
  status: 'IN_PROGRESS' | 'DONE' | 'COMPLETE';
  parent_task_id: number | null;
  dependencyCount?: number;
  doneCount?: number; 
  completeCount?: number;
}

const TaskList: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [filter, setFilter] = useState<'ALL' | 'IN_PROGRESS' | 'DONE' | 'COMPLETE'>('ALL');

  // Fetch tasks from the backend
  const fetchTasks = async () => {
    try {
      const response = await fetch('http://localhost:5000/tasks');
      if (!response.ok) {
        throw new Error('Failed to fetch tasks');
      }
      const data: Task[] = await response.json();

      // Fetch dependency counts for each task
      const tasksWithDependencies = await Promise.all(
        data.map(async (task) => {
          const dependencyCount = await fetchDependencyCount(task.id);
          const doneCount = await fetchDoneCount(task.id);
          const completeCount = await fetchCompleteCount(task.id);
          return { ...task, dependencyCount, doneCount, completeCount };
        })
      );

      setTasks(tasksWithDependencies);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  // Fetch dependency count for a single task
  const fetchDependencyCount = async (taskId: number) => {
    try {
      const response = await fetch(`http://localhost:5000/tasks/${taskId}/dependencies`);
      if (!response.ok) {
        throw new Error('Failed to fetch dependency count');
      }
      const data = await response.json();
      return data.dependencyCount || 0;
    } catch (error) {
      console.error('Error fetching dependency count:', error);
      return 0;
    }
  };

// Toggle task status
const toggleTaskStatus = async (task: Task) => {
  let updatedStatus: 'IN_PROGRESS' | 'DONE' | 'COMPLETE';

  const children = tasks.filter(t => t.parent_task_id === task.id);

  if (children.length === 0) {
    // No children, mark as COMPLETE when ticked
    updatedStatus = task.status === 'COMPLETE' ? 'IN_PROGRESS' : 'COMPLETE';
  } else {
    // If it has children, check their status
    const allComplete = children.every(child => child.status === 'COMPLETE');
    const someInProgress = children.some(child => child.status === 'IN_PROGRESS');

    if (task.status === 'COMPLETE') {
      updatedStatus = 'IN_PROGRESS'; // Toggle back to IN_PROGRESS if already COMPLETE
    } else if (someInProgress) {
      updatedStatus = 'DONE'; // Some children are IN_PROGRESS, mark as DONE
    } else {
      updatedStatus = 'COMPLETE'; // All children are COMPLETE, mark as COMPLETE
    }
  }

    try {
      // Update task status in the backend
      const response = await fetch(`http://localhost:5000/tasks/${task.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ...task, status: updatedStatus }),
      });

      if (!response.ok) {
        throw new Error('Failed to update task status');
      }

      // Update the local state immediately
      setTasks(prevTasks =>
        prevTasks.map(t => {
          if (t.id === task.id) {
            return { ...t, status: updatedStatus };
          }
          return t;
        })
      );

      // Check if we need to update the parent task
      const parentId = task.parent_task_id;
      if (parentId) {
        const siblings = tasks.filter(t => t.parent_task_id === parentId);
        const allComplete = siblings.every(sibling => sibling.status === 'COMPLETE');

        // If all siblings are complete, update the parent task to COMPLETE
        if (allComplete) {
          await fetch(`http://localhost:5000/tasks/${parentId}`, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ status: 'COMPLETE' }), // Assuming the parent needs to be updated
          });

          // Update the parent task status in the local state
          setTasks(prevTasks =>
            prevTasks.map(t => {
              if (t.id === parentId) {
                return { ...t, status: 'COMPLETE' };
              }
              return t;
            })
          );
        }
      }

      // Optionally, fetch all tasks again if you want to ensure consistency
      // await fetchTasks();

      window.location.reload();
    } catch (error) {
      console.error('Error updating task status:', error);
      alert('Failed to update task status. Please try again.');
    }
  };

  // Filter tasks based on selected status
  const filteredTasks = filter === 'ALL' ? tasks : tasks.filter(task => task.status === filter);

  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div>
      <h1>Task Listing</h1>
      <label>
        Filter by status:
        <select value={filter} onChange={(e) => setFilter(e.target.value as 'ALL' | 'IN_PROGRESS' | 'DONE' | 'COMPLETE')}>
          <option value="ALL">All</option>
          <option value="IN_PROGRESS">IN PROGRESS</option>
          <option value="DONE">DONE</option>
          <option value="COMPLETE">COMPLETE</option>
        </select>
      </label>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Status</th>
            <th>Parent Task ID</th>
            <th>Total Number of Dependencies</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredTasks.map(task => (
            <tr key={task.id}>
              <td>{task.id}</td>
              <td>{task.name}</td>
              <td>{task.status}</td>
              <td>{task.parent_task_id !== null ? task.parent_task_id : 'N/A'}</td>
              <td>{task.dependencyCount !== undefined ? task.dependencyCount : '0'}</td>
              <td>
                <label>
                  <input
                    type="checkbox"
                    checked={task.status === 'DONE' || task.status === 'COMPLETE'}
                    onChange={() => toggleTaskStatus(task)}
                  />
                  {task.status === 'DONE' || task.status === 'COMPLETE' ? ' Mark as IN PROGRESS' : ' Mark as DONE'}
                </label>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Sidebar component
const Sidebar: React.FC = () => (
  <nav className="sidebar">
    <ul>
      <li><Link to="/">Task Listing</Link></li>
      <li><Link to="/taskformpage">Task Form</Link></li>
    </ul>
  </nav>
);

// Main App component with Router and Sidebar
const App: React.FC = () => {
  return (
    <Router>
      <div className="app-container">
        <Sidebar />
        <div className="content">
          <Routes>
            <Route path="/" element={<TaskList />} />
            <Route path="/taskformpage" element={<TaskFormPage />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
