// src/DashboardPage.tsx

import React, { useState } from 'react';

const TaskCreationForm: React.FC = () => {
  const [taskName, setTaskName] = useState('');
  const [parentTaskID, setParentTaskID] = useState(''); // New state for Parent Task ID
  const [error, setError] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');

    if (!taskName) {
      setError('Task name is required');
      return;
    }

    // Optionally, you can validate if parentTaskID is a valid format
    // if (parentTaskID && !/T\d{2}/.test(parentTaskID)) {
    //   setError('Invalid Parent Task ID format');
    //   return;
    // }

    try {
      const response = await fetch('http://localhost:5000/tasks', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name: taskName, parentTaskID }), // Include parentTaskID in the request body
      });

      if (!response.ok) {
        throw new Error('Failed to create task');
      }

      const newTask = await response.json();
      console.log('Task created:', newTask);

      // Clear the form after successful task creation
      setTaskName('');
      setParentTaskID(''); // Clear Parent Task ID input
    } catch (error) {
      setError('Error creating task');
      console.error('Error creating task:', error);
    }
  };

  return (
    <div>
      <h2>Create a New Task</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <label>
          Task Name:
          <input
            type="text"
            value={taskName}
            onChange={(e) => setTaskName(e.target.value)}
            required
          />
        </label>
        <br />
        <label>
          Parent Task ID:
          <input
            type="text"
            value={parentTaskID}
            onChange={(e) => setParentTaskID(e.target.value)}
            placeholder="T01, T02..."
          />
        </label>
        <br />
        <button type="submit">Create Task</button>
      </form>
    </div>
  );
};

export default TaskCreationForm;
