import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import './App.css';
import TaskFormPage from './pages/TaskFormPage';

interface Task {
  id: number;
  name: string;
  status: 'IN_PROGRESS' | 'DONE' | 'COMPLETE';
  parent_task_id: number | null;
  dependencyCount?: number;
  doneCount?: number;
  completeCount?: number;
  children?: Task[]; // New field for child tasks
}

const TaskList: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [filter, setFilter] = useState<'ALL' | 'IN_PROGRESS' | 'DONE' | 'COMPLETE'>('ALL');

  const fetchTasks = async () => {
    try {
      const response = await fetch('http://localhost:5000/tasks');
      if (!response.ok) {
        throw new Error('Failed to fetch tasks');
      }
      const data: Task[] = await response.json();
      const hierarchicalTasks = buildHierarchy(data); // Build hierarchical structure
      setTasks(hierarchicalTasks);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const buildHierarchy = (tasks: Task[]) => {
    const taskMap: { [key: number]: Task } = {};
    tasks.forEach(task => {
      task.children = []; // Initialize children array
      taskMap[task.id] = task; // Map task by ID
    });

    const hierarchicalTasks: Task[] = [];
    tasks.forEach(task => {
      if (task.parent_task_id) {
        const parent = taskMap[task.parent_task_id];
        if (parent) {
          parent.children.push(task); // Add task to parent's children
        }
      } else {
        hierarchicalTasks.push(task); // Top-level task
      }
    });

    return hierarchicalTasks;
  };

  const toggleTaskStatus = async (task: Task) => {
    // ... (existing toggle logic)
  };

  const filteredTasks = filter === 'ALL' ? tasks : tasks.filter(task => task.status === filter);

  useEffect(() => {
    fetchTasks();
  }, []);

  const renderTasks = (taskList: Task[]) => {
    return (
      <ul>
        {taskList.map(task => (
          <li key={task.id}>
            <div>
              <span>{task.name} - {task.status}</span>
              <label>
                <input
                  type="checkbox"
                  checked={task.status === 'DONE' || task.status === 'COMPLETE'}
                  onChange={() => toggleTaskStatus(task)}
                />
                {task.status === 'DONE' || task.status === 'COMPLETE' ? ' Mark as IN PROGRESS' : ' Mark as DONE'}
              </label>
            </div>
            {task.children && task.children.length > 0 && renderTasks(task.children)} {/* Recursively render children */}
          </li>
        ))}
      </ul>
    );
  };

  return (
    <div>
      <h1>Task Listing</h1>
      <label>
        Filter by status:
        <select value={filter} onChange={(e) => setFilter(e.target.value as 'ALL' | 'IN_PROGRESS' | 'DONE' | 'COMPLETE')}>
          <option value="ALL">All</option>
          <option value="IN_PROGRESS">IN PROGRESS</option>
          <option value="DONE">DONE</option>
          <option value="COMPLETE">COMPLETE</option>
        </select>
      </label>
      {renderTasks(filteredTasks)} {/* Render tasks recursively */}
    </div>
  );
};

// Sidebar component
const Sidebar: React.FC = () => (
  <nav className="sidebar">
    <ul>
      <li><Link to="/">Task Listing</Link></li>
      <li><Link to="/taskformpage">Task Form</Link></li>
    </ul>
  </nav>
);

// Main App component with Router and Sidebar
const App: React.FC = () => {
  return (
    <Router>
      <div className="app-container">
        <Sidebar />
        <div className="content">
          <Routes>
            <Route path="/" element={<TaskList />} />
            <Route path="/taskformpage" element={<TaskFormPage />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
